function recompile%this dempnstrates that a function can be made to recompile another function in%the appropriate context.rmpath('Phi-X174:eyetracking:library:matlab5_compensation:');clear testertry	tester	disp('no error');catch	disp(['1. error ' lasterr]);endwhoaddpath('Phi-X174:eyetracking:library:matlab5_compensation:');try	tester;	disp('no error');catch	disp(['2. error ' lasterr]);endclear tester;whotry	tester;	disp('no error');catch	disp(['3. unexpected error ' lasterr]);endclear tester;try	feval('tester');	disp('no error');catch	disp(['4. unexpected error ' lasterr]);endrmpath('Phi-X174:eyetracking:library:matlab5_compensation:');clear tester;try	feval('tester');	disp('no error');catch	disp(['5. error ' lasterr]);end