function n = numel(a, varargin)%workalike for numel builtin of newer versions of matlab.if nargin > 1	class(varargin)	a = subsref(a, struct('type', '()', 'subs', varargin));endn = prod(size(a));%tests:%>> a = rand(100, 100, 100);%>> numel(a)%ans =%     1000000%>> numel(a, 4892)%ans =%     1%>> numel(a, 2, 5, 4)%ans =%     1%>> numel(a, 2, ':', 4)%ans =%   100%>> numel(a, 2, ':', 4:6)%ans =%   300