% measure overhead of getkeyforeyelink functionkey=0;t0=getsecs;i=1;el=initeyelinkdefaults;fprintf( '\nPress ''%s'' + ''%s'' to stop test.\n', kbname(el.modifierkey), kbname(el.quitkey) );while key~=el.TERMINATE_KEY	t1=getsecs;	key=getkeyforeyelink(el);	t(i)=getsecs-t1;	i=i+1;	%waitsecs(.1);endt2=getsecs-t0;fprintf( '\nMean time for getkeyforeyelink: %.3f ms\n', mean(t)*1000 );fprintf( '\nTotal test time: %.3f s\n', t2 );