% BitsPlusColorImageTest% % Lets see if we can tame Color++ mode.%% 8/8/04	dhb		Started it.% Clearclear all;% First need an interesting test image.% Load it in.  This is an RGB image with% data in range 0-1.  fprintf('Loading a high dynamic range test image\n');load colorPlusTestfprintf('Converting to color++ format\n');[nil,temp1] = BitsPlusPackColorImage(theImage);packedImage = BitsPlusPackColorImage(temp1,0,0);% Show the image[m,n,p] = size(packedImage);rect = [0 0 m n];fprintf('Showing image\n');[window,rect] = Screen(1,'OpenWindow',0,[],32);identityClut = (0:255)'*ones(1,3);Screen(window,'SetClut',identityClut);Screen(window,'PutImage',packedImage,[0 0 n m]);fprintf('Hit any character to continue\n');GetChar;Screen(window,'Close'); 	 	